import java.util.Scanner;
public class MaipulatingNumbers {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner input = new Scanner(System.in);
		System.out.println("Please enter two numbers");
		int n1 = input.nextInt();
		int n2 = input.nextInt();
		
		System.out.println("The sum is " + (int)(n1 + n2) + ".");
		System.out.println("The difference is " + (int)(n1 - n2) + ".");
		System.out.println("The product is " + (int)(n1 * n2) + ".");
		System.out.println("The quotient is " + (int)(n1 / n2) + ".");
		System.out.println("The remainder is " + (int)(n1 % n2) + ".");
		System.out.println();
		System.out.println("The sum is " + (float)(n1 +n2) + ".");
		System.out.println("The difference is " + (float)(n1 -n2) + ".");
		System.out.println("The product is " + (float)(n1 *n2) + ".");
		System.out.println("The quotient is " + (float)(n1 /n2) + ".");
		System.out.println("The remainder is " + (float)(n1 %n2) + ".");
		System.out.println();
		System.out.println("The sum is " + (double)(n1 +n2) + ".");
		System.out.println("The difference is " + (double)(n1 -n2) + ".");
		System.out.println("The product is " + (double)(n1 *n2) + ".");
		System.out.println("The quotient is " + (double)(n1 /n2) + ".");
		System.out.println("The remainder is " + (double)(n1 %n2) + ".");
		System.out.println();
		System.out.println("The sum is " + (short)(n1 +n2) + ".");
		System.out.println("The difference is " + (short)(n1 -n2) + ".");
		System.out.println("The product is " + (short)(n1 *n2) + ".");
		System.out.println("The quotient is " + (short)(n1 /n2) + ".");
		System.out.println("The remainder is " + (short)(n1 %n2) + ".");
		
	
	
		//CSE 2 first floor office hours
		
	}

}
